public void vezjogador(){
    if (jogador1==true&&jogador2==true) {
        this.vez=1;
        jogador1=false;
        jogador2=true;
        txtvisor.setText("Vez do Jogador O");
        vez++;
        //se o jogador2 for false significa que o ultimo foi X
    }else if (jogador1==true&&jogador2==false){
        this.vez=1;
        jogador1=false;
        jogador2=true;
        txtvisor.setText("Vez do Jogador O");
        vez++;
    }else{
        this.vez=2;
        jogador1=true;
        jogador2=false;
        txtvisor.setText("Vez do Jogador X");
        vez--;
        }
}
