    public void limparmatriz() {
        for (linha = 0; linha < 3; linha++) {
            for (coluna = 0; coluna < 3; coluna++) {
                posicaomatriz[linha][coluna] = "";
            }
        }
    }
public void verificaposicoes(){
    contjogada=0;
    for(linha=0;linha<3;linha++){
        for(coluna=0;coluna<3;coluna++){
          if(posicaomatriz[linha][coluna]=="x"|posicaomatriz[linha][coluna]=="o"){
             contjogada+=1;
             System.out.println(contjogada);
          }
        }
        }
}
public void vencedor(){
        linha1=posicaomatriz[0][0]+posicaomatriz[0][1]+posicaomatriz[0][2];
        linha2=posicaomatriz[1][0]+posicaomatriz[1][1]+posicaomatriz[1][2];
        linha3=posicaomatriz[2][0]+posicaomatriz[2][1]+posicaomatriz[2][2];
   
        coluna1=posicaomatriz[0][0]+posicaomatriz[1][0]+posicaomatriz[2][0];
        coluna2=posicaomatriz[0][1]+posicaomatriz[1][1]+posicaomatriz[2][1];
        coluna3=posicaomatriz[0][2]+posicaomatriz[1][2]+posicaomatriz[2][2];
   
        diagonal1=posicaomatriz[0][0]+posicaomatriz[1][1]+posicaomatriz[2][2];
        diagonal2=posicaomatriz[0][2]+posicaomatriz[1][1]+posicaomatriz[2][0];
        
    if(linha1.equals("xxx")||linha2.equals("xxx")||linha3.equals("xxx")
            ||coluna1.equals("xxx")||coluna2.equals("xxx")||coluna3.equals("xxx")
            ||diagonal1.equals("xxx")||diagonal2.equals("xxx")){
        Fecharbotoes();
        txtvisor.setText("O vencedor é o jogador X");
        //txtvisor2.setText();

    }else if(linha1.equals("ooo")||linha2.equals("ooo")||linha3.equals("ooo")
            ||coluna1.equals("ooo")||coluna2.equals("ooo")||coluna3.equals("ooo")
            ||diagonal1.equals("ooo")||diagonal2.equals("ooo")){
        Fecharbotoes();
    txtvisor.setText("O vencedor é o jogador O");
    }else if(contjogada==9){
        txtvisor.setText("Deu Velha");
    }
}
